import React, { useEffect } from 'react';
import Navbar from '../components/Navbar';
import '../styles/car-details.css';

const CarDetailsPage = () => {
  useEffect(() => {
    const params = new URLSearchParams(window.location.search);
    const trim_id = params.get("trim_id");
    const make = params.get("make");
    const model = params.get("model");
    const year = params.get("year");
    const image = decodeURIComponent(params.get("image") || "");
    const price = params.get("price");
    const location = params.get("location");

    document.getElementById("carTitle").textContent = `${year} ${make} ${model}`;
    document.getElementById("carImage").src = image;
    document.getElementById("carPrice").textContent = `$${price} / day`;
    document.getElementById("pickupLocation").value = location;
    document.getElementById("dropoffLocation").value = location;

    fetch(`http://localhost:5000/api/trim-details/${trim_id}`)
      .then(res => res.json())
      .then(data => {
        document.getElementById("carTypes").textContent = data.bodies?.[0]?.type || "N/A";
        document.getElementById("carSeats").textContent = data.bodies?.[0]?.seats || "N/A";
        document.getElementById("carDoors").textContent = data.bodies?.[0]?.doors || "N/A";

        document.getElementById("carFuel").textContent = data.engines?.[0]?.fuel_type || "N/A";
        document.getElementById("carTank").textContent = data.engines?.[0]?.fuel_tank || "N/A";

        document.getElementById("carTransmission").textContent = data.transmissions?.[0]?.description || "N/A";
        document.getElementById("carDrive").textContent = data.drive_types?.[0]?.description || "N/A";

        const mpg = data.engines?.[0]?.mileage_city;
        if (mpg) {
          const kmPerLitre = (235.214583 / mpg).toFixed(2);
          document.getElementById("carMPG").textContent = `${kmPerLitre} L/100km`;
        }

        const tankGal = data.engines?.[0]?.fuel_tank;
        if (tankGal) {
          const litres = (tankGal * 3.785).toFixed(0);
          document.getElementById("carTank").textContent = `${litres} litres`;
        }

        const height = data.dimensions?.height;
        const length = data.dimensions?.length;
        if (height && length) {
          const cmHeight = (height * 2.54).toFixed(0);
          const cmLength = (length * 2.54).toFixed(0);
          document.getElementById("carHeight").textContent = `${cmHeight} cm`;
          document.getElementById("carLength").textContent = `${cmLength} cm`;
        }

        // ✅ ORIGINAL BLOCK – Dynamic Full Spec HTML
        const specsEl = document.getElementById("specBox");
        if (specsEl) {
          specsEl.innerHTML = `
            <div class="category">Pricing</div>
            <div class="specs-grid">
              <p><strong>MSRP:</strong> $${data.msrp || "N/A"}</p>
            </div>

            <div class="category">Performance</div>
            <div class="specs-grid">
              <p><strong>Horsepower:</strong> ${data.engines?.[0]?.horsepower_hp || "N/A"} hp</p>
              <p><strong>Torque:</strong> ${data.engines?.[0]?.torque_lb_ft || "N/A"} lb-ft</p>
              <p><strong>0-60 mph:</strong> ${data.acceleration || "N/A"} sec</p>
              <p><strong>Top Speed:</strong> ${data.top_speed || "N/A"} mph</p>
              <p><strong>Horsepower:</strong> ${data.engines?.[0]?.horsepower_hp || "N/A"} hp</p>
              <p><strong>Torque:</strong> ${data.engines?.[0]?.torque_ft_lbs || "N/A"} ft-lbs</p>
              <p><strong>Engine Type:</strong> ${data.engines?.[0]?.engine_type || "N/A"}</p>
              <p><strong>Cylinders:</strong> ${data.engines?.[0]?.cylinders || "N/A"}</p>
              <p><strong>Cam Type:</strong> ${data.engines?.[0]?.cam_type || "N/A"}</p>
              <p><strong>Drive Type:</strong> ${data.drive_types?.[0]?.description || "N/A"}</p>
              <p><strong>Transmission:</strong> ${data.transmissions?.[0]?.description || "N/A"}</p>
            </div>

            <div class="category">Dimensions</div>
            <div class="specs-grid">
              <p><strong>Height:</strong> ${data.dimensions?.height ? (data.dimensions.height * 2.54).toFixed(0) + " cm" : "N/A"}</p>
              <p><strong>Length:</strong> ${data.dimensions?.length ? (data.dimensions.length * 2.54).toFixed(0) + " cm" : "N/A"}</p>
              <p><strong>Wheelbase:</strong> ${data.dimensions?.wheelbase || "N/A"} in</p>
              <p><strong>Ground Clearance:</strong> ${data.dimensions?.ground_clearance || "N/A"} in</p>
            </div>
          `;
        }
      })
      .catch(err => {
        console.error("Trim fetch failed", err);
        const specBox = document.getElementById("specBox");
        if (specBox) {
          specBox.innerHTML = "<p>Failed to load specifications</p>";
        }
      });

    const toggleBtn = document.getElementById("toggleSpecs");
    const specBox = document.getElementById("specBox");
    if (toggleBtn && specBox) {
      toggleBtn.addEventListener("click", () => {
        if (specBox.style.display === "none" || specBox.style.display === "") {
          specBox.style.display = "block";
          toggleBtn.textContent = "Hide Details ⬆️";
        } else {
          specBox.style.display = "none";
          toggleBtn.textContent = "Show More Details ⬇️";
        }
      });
    }
  }, []);

    return (
    <>
      <Navbar />
      <div className="car-details-page">
        <h1 id="carTitle">Loading...</h1>

        <div className="main-box">
          {/* LEFT SECTION */}
          <div className="car-preview">
            <img id="carImage" src="" alt="Car Preview" />
            <p id="carPrice" className="daily-price">$-- / day</p>
          </div>

          {/* RIGHT SECTION */}
          <div className="car-specs">
            <h2>Car Specifications</h2>
            <div className="spec-list">
              <li><strong>Type:</strong> <span id="carTypes">-</span></li>
              <li><strong>Seats:</strong> <span id="carSeats">-</span></li>
              <li><strong>Doors:</strong> <span id="carDoors">-</span></li>
              <li><strong>Fuel Type:</strong> <span id="carFuel">-</span></li>
              <li><strong>Transmission:</strong> <span id="carTransmission">-</span></li>
              <li><strong>Drive:</strong> <span id="carDrive">-</span></li>
              <li><strong>City Mileage:</strong> <span id="carMPG">-</span></li>
              <li><strong>Tank Size:</strong> <span id="carTank">-</span></li>
              <li><strong>Height:</strong> <span id="carHeight">-</span></li>
              <li><strong>Length:</strong> <span id="carLength">-</span></li>
            </div>

            <button id="toggleSpecs" className="toggle-button">Show More Details ⬇️</button>
            <div id="specBox" className="specs-section" style={{ display: 'none' }}>
              {/* Dynamic full spec block inserted here */}
            </div>
          </div>
        </div>
        
        {/* BOOKING SECTION */}
        <div className="booking-box">
          <h2>Book This Car</h2>
          <form id="bookingForm">
            <label htmlFor="pickupDate">Pickup Date</label>
            <input type="date" id="pickupDate" name="pickupDate" required />

            <label htmlFor="dropoffDate">Drop-off Date</label>
            <input type="date" id="dropoffDate" name="dropoffDate" required />

            <label htmlFor="pickupLocation">Pickup Location</label>
            <input type="text" id="pickupLocation" name="pickupLocation" required readOnly />

            <label htmlFor="dropoffLocation">Drop-off Location</label>
            <input type="text" id="dropoffLocation" name="dropoffLocation" required readOnly />

            <button type="submit">Confirm Booking</button>
          </form>
        </div>
      </div>
    </>
  );
};

export default CarDetailsPage;
