import { useEffect, useRef, useState } from 'react';
import '../styles/carlist.css';
import '../styles/navbar.css';

export default function CarListPage() {
  const [cars, setCars] = useState([]);
  const [filtered, setFiltered] = useState([]);
  const [query, setQuery] = useState('');
  const [filters, setFilters] = useState({
    type: '', brand: '', location: '', seats: '', minPrice: 0, maxPrice: Infinity
  });
  const [showFilter, setShowFilter] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  const carsPerPage = 10;
  const trimDetailsCache = useRef(new Map());

  useEffect(() => {
    fetch('http://localhost:5000/api/cars')
      .then(res => {
        if (!res.ok) throw new Error('Failed to fetch cars');
        return res.json();
      })
      .then(async response => {
        const baseCars = Array.isArray(response) ? response : response.data;

        const enriched = [];
        const maxCars = 15;
        let index = 0;

        while (enriched.length < maxCars && index < baseCars.length) {
          const car = baseCars[index];
          index++;

          try {
            if (trimDetailsCache.current.has(car.id)) {
              const details = trimDetailsCache.current.get(car.id);
              enriched.push({
                ...car,
                msrp: details.msrp,
                seats: details.bodies?.[0]?.seats,
                fuel_type: details.engines?.[0]?.fuel_type,
                transmission: details.transmissions?.[0]?.description,
                location: details.location || 'Unknown',
                type: details.bodies?.[0]?.type || 'Unknown'
              });
              continue;
            }

            const res = await fetch(`http://localhost:5000/api/trim-details/${car.id}`);
            if (res.status === 402) {
              console.warn(`Skipping trim ${car.id} due to 402`);
              continue;
            }
            if (!res.ok) throw new Error(`HTTP ${res.status}`);
            const details = await res.json();
            trimDetailsCache.current.set(car.id, details);

            enriched.push({
              ...car,
              msrp: details.msrp,
              seats: details.bodies?.[0]?.seats,
              fuel_type: details.engines?.[0]?.fuel_type,
              transmission: details.transmissions?.[0]?.description,
              location: details.location || 'Unknown',
              type: details.bodies?.[0]?.type || 'Unknown'
            });
          } catch (err) {
            console.warn(`Trim ${car.id} failed: ${err.message}`);
          }
        }

        setCars(enriched);
        setFiltered(enriched);
      })
      .catch(err => {
        console.error('Error fetching cars:', err);
      });
  }, []);

  useEffect(() => {
    const filteredCars = cars.filter(car => {
      const make = (car.make || '').toLowerCase();
      const msrp = car.msrp || 20000;
      const carType = (car.body_type || car.type || '').toLowerCase();
      const carSeats = parseInt(car.seats) || 0;

      const typeMatch = !filters.type || carType === filters.type.toLowerCase();
      const brandMatch = !filters.brand || make === filters.brand.toLowerCase();
      const priceMatch = msrp / 100 >= filters.minPrice && msrp / 100 <= filters.maxPrice;
      const seatMatch = !filters.seats || parseInt(filters.seats) === carSeats;
      const locationMatch = !filters.location || car.location === filters.location;

      return typeMatch && brandMatch && priceMatch && seatMatch && locationMatch;
    });

    setFiltered(filteredCars);
    setCurrentPage(1);
  }, [filters, cars]);

  const paginated = filtered.slice((currentPage - 1) * carsPerPage, currentPage * carsPerPage);

  return (
    <> 
    <header>
      <div className="navbar">
        <img src={logo} alt="Logo" />
        <div className="center-nav">
          <a href="/">Home</a>
          <a href="/carlist">Carlist</a>
          <a href="/about">About Us</a>
          <a id="accountLink" href="/login">Account</a>
        </div>
        <div className="social">
          <a href="#"><i className="fa-solid fa-magnifying-glass"></i></a>
          <a href="#"><i className="fa-solid fa-bookmark"></i></a>
          <a href="#"><i className="fa-solid fa-bell"></i></a>
        </div>
      </div>
    </header>
    <div className="layout">
      <aside className={`sidebar ${!showFilter ? 'hidden' : ''}`}>
        <button className="close-button" onClick={() => setShowFilter(false)}>✖</button>
        <h2>Filter By</h2>
        <div className="filter-group">
          <label htmlFor="carType">Car Type</label>
          <select id="carType" onChange={e => setFilters(f => ({ ...f, type: e.target.value }))}>
            <option value="">All</option>
            <option value="Sedan">Sedan</option>
            <option value="SUV">SUV</option>
            <option value="Coupe">Coupe</option>
            <option value="Convertible">Convertible</option>
            <option value="Wagon">Wagon</option>
            <option value="Hatchback">Hatchback</option>
            <option value="Van">Van</option>
          </select>
        </div>
        <div className="filter-group">
          <label>Price Range</label>
          <div className="price-inputs">
            <input type="number" placeholder="Min" onChange={e => setFilters(f => ({ ...f, minPrice: parseInt(e.target.value) || 0 }))} />
            <span>-</span>
            <input type="number" placeholder="Max" onChange={e => setFilters(f => ({ ...f, maxPrice: parseInt(e.target.value) || Infinity }))} />
          </div>
        </div>
        <div className="filter-group">
          <label htmlFor="carBrand">Car Brand</label>
          <select id="carBrand" onChange={e => setFilters(f => ({ ...f, brand: e.target.value }))}>
            <option value="">All</option>
            <option value="BMW">BMW</option>
            <option value="Toyota">Toyota</option>
            <option value="Ford">Ford</option>
            <option value="Tesla">Tesla</option>
          </select>
        </div>
        <div className="filter-group">
          <label htmlFor="carSeats">Seats</label>
          <select id="carSeats" onChange={e => setFilters(f => ({ ...f, seats: e.target.value }))}>
            <option value="">Any</option>
            <option value="2">2</option>
            <option value="4">4</option>
            <option value="5">5</option>
            <option value="7">7</option>
          </select>
        </div>
        <div className="filter-group">
          <label htmlFor="locationFilter">Location</label>
          <select id="locationFilter" onChange={e => setFilters(f => ({ ...f, location: e.target.value }))}>
            <option value="">All</option>
            <option value="Kuala Lumpur">Kuala Lumpur</option>
            <option value="Penang">Penang</option>
            <option value="Johor Bahru">Johor Bahru</option>
            <option value="Ipoh">Ipoh</option>
            <option value="Melaka">Melaka</option>
            <option value="Seremban">Seremban</option>
            <option value="Kuching">Kuching</option>
            <option value="Kota Kinabalu">Kota Kinabalu</option>
          </select>
        </div>
        <button className="apply-button" onClick={() => setShowFilter(false)}>Apply Filter</button>
      </aside>

      <main className={`main-content ${showFilter ? 'sidebar-visible' : ''}`}>
        <h1>Available Cars</h1>
        <div className="search-container">
          <input type="text" placeholder="Search by make or model..." onChange={e => setQuery(e.target.value.toLowerCase())} />
          <button id="searchButton">Search</button>
        </div>
        <div className="filter-toggle-container">
          <button className="open-button" onClick={() => setShowFilter(true)}>Filter Options</button>
        </div>

        <div className="car-list">
          {paginated.filter(car => `${car.make} ${car.name}`.toLowerCase().includes(query)).map(car => {
            const image = car.image || 'https://placehold.co/300x180?text=No+Image';
            const price = Math.floor((car.msrp || 25000) / 100 / 30);
            return (
              <div key={car.id} className="car-card">
                <img src={image} alt={`${car.make} ${car.name}`} onError={e => e.currentTarget.src = '/images/placeholder.jpg'} />
                <div className="card-content">
                  <h3>{car.make} {car.name}</h3>
                  <span className="year-badge">{car.year}</span>
                  <div className="card-icons">
                    <div>👥 {car.seats} People</div>
                    <div>⚡ {car.fuel_type}</div>
                  </div>
                  <div className="card-icons">
                    <div>🔧 {car.transmission}</div>
                  </div>
                  <div className="card-icons">
                    <div>📍 {car.location}</div>
                  </div>
                  <div className="price-rent">
                    <div className="price">${price} / day</div>
                    <a href={`/car-details?trim_id=${car.id}`} className="btn rent-now">
                      <button>Rent Now</button>
                    </a>
                  </div>
                </div>
              </div>
            );
          })}
        </div>

        {filtered.length === 0 && <p className="no-results">No cars found matching the filter criteria.</p>}

        <div className="pagination-controls">
          {Array.from({ length: Math.ceil(filtered.length / carsPerPage) }, (_, i) => (
            <button
              key={i + 1}
              className={currentPage === i + 1 ? 'active' : ''}
              onClick={() => setCurrentPage(i + 1)}>
              {i + 1}
            </button>
          ))}
        </div>
      </main>
    </div>
    </>
  );
}
