import React, { useEffect } from 'react';
import Navbar from '../components/Navbar';
import '../styles/car-details.css';

const CarDetailsPage = () => {
  useEffect(() => {
    setTimeout(() => {
      const params = new URLSearchParams(window.location.search);
      const trim_id = params.get("trim_id");
      const make = params.get("make");
      const model = params.get("model");
      const year = params.get("year");
      const image = decodeURIComponent(params.get("image") || "");
      const price = params.get("price");
      const location = params.get("location");

      const token = localStorage.getItem("token");
      const userId = localStorage.getItem("userId");

      document.getElementById("carTitle").textContent = `${year} ${make} ${model}`;
      document.getElementById("carImage").src = image;
      document.getElementById("carPrice").textContent = `$${price} / day`;
      document.getElementById("pickupLocation").value = location;
      document.getElementById("dropoffLocation").value = location;
      document.getElementById("carYear").textContent = year;

      // 🖼️ Fallback if placeholder image
      if (image.includes("no-photo")) {
        fetch(`http://localhost:5000/api/car-image?make=${make}&model=${model}&year=${year}`)
          .then(res => res.json())
          .then(data => {
            if (data?.url) {
              document.getElementById("carImage").src = data.url;
            }
          })
          .catch(err => console.error("Fallback image fetch failed", err));
      }

      // ❤️ Wishlist check
      if (token && userId) {
        fetch(`http://localhost:5000/api/users/${userId}/wishlist`, {
          headers: { Authorization: `Bearer ${token}` }
        })
          .then(res => res.json())
          .then(data => {
            const isWished = data.some(car => car.trim_id == trim_id);
            if (isWished) {
              document.getElementById("wishlistBtn").textContent = "💔 Remove from Wishlist";
            }
          });
      }

      // ❤️ Wishlist toggle
      document.getElementById("wishlistBtn").addEventListener("click", () => {
        if (!token || !userId) {
          alert("Please log in to use wishlist.");
          return;
        }

        const action = document.getElementById("wishlistBtn").textContent.includes("Remove")
          ? "remove"
          : "add";

        fetch(`http://localhost:5000/api/users/${userId}/wishlist`, {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`
          },
          body: JSON.stringify({ trim_id, action })
        })
          .then(res => res.json())
          .then(() => {
            document.getElementById("wishlistBtn").textContent =
              action === "add" ? "💔 Remove from Wishlist" : "🤍 Add to Wishlist";
          });
      });
            // 🚗 Fetch trim details
      fetch(`http://localhost:5000/api/trim-details/${trim_id}`)
        .then(res => res.json())
        .then(data => {
          document.getElementById("carTypes").textContent = data.bodies?.[0]?.type || "N/A";
          document.getElementById("carSeats").textContent = data.bodies?.[0]?.seats || "N/A";
          document.getElementById("carDoors").textContent = data.bodies?.[0]?.doors || "N/A";
          document.getElementById("carFuel").textContent = data.engines?.[0]?.fuel_type || "N/A";
          document.getElementById("carTransmission").textContent = data.transmissions?.[0]?.description || "N/A";
          document.getElementById("carDrive").textContent = data.drive_types?.[0]?.description || "N/A";

          const mpg = data.engines?.[0]?.mileage_city;
          if (mpg) {
            const kmpl = (235.214583 / mpg).toFixed(2);
            document.getElementById("carMPG").textContent = `${kmpl} L/100km`;
          }

          const tank = data.engines?.[0]?.fuel_tank;
          if (tank) {
            const litres = (tank * 3.785).toFixed(0);
            document.getElementById("carTank").textContent = `${litres} litres`;
          }

          const height = data.dimensions?.height;
          const length = data.dimensions?.length;
          if (height && length) {
            document.getElementById("carHeight").textContent = `${(height * 2.54).toFixed(0)} cm`;
            document.getElementById("carLength").textContent = `${(length * 2.54).toFixed(0)} cm`;
          }

          // 🧾 Specs innerHTML
          const specsEl = document.getElementById("carSpecs");
          if (specsEl) {
            specsEl.innerHTML = `
                <div class="category">Pricing</div>
                <div class="specs-grid">
                <p><strong>MSRP:</strong> $${data.msrp || "N/A"}</p>
                <p><strong>Invoice:</strong> $${data.invoice || "N/A"}</p>
                </div>

                <div class="category">Engine</div>
                <div class="specs-grid">
                <p><strong>Engine Type:</strong> ${data.engines?.[0]?.engine_type || "N/A"}</p>
                <p><strong>Cylinders:</strong> ${data.engines?.[0]?.cylinders || "N/A"}</p>
                <p><strong>Cam Type:</strong> ${data.engines?.[0]?.cam_type || "N/A"}</p>
                <p><strong>Horsepower:</strong> ${data.engines?.[0]?.horsepower_hp || "N/A"} hp</p>
                <p><strong>Torque:</strong> ${data.engines?.[0]?.torque_lb_ft || "N/A"} lb-ft</p>
                </div>

                <div class="category">Fuel Economy</div>
                <div class="specs-grid">
                <p><strong>City MPG:</strong> ${data.engines?.[0]?.mileage_city || "N/A"}</p>
                <p><strong>Highway MPG:</strong> ${data.engines?.[0]?.mileage_highway || "N/A"}</p>
                <p><strong>Fuel Tank:</strong> ${data.engines?.[0]?.fuel_tank ? (data.engines[0].fuel_tank * 3.785).toFixed(1) + " L" : "N/A"}</p>
                </div>

                <div class="category">Dimensions</div>
                <div class="specs-grid">
                <p><strong>Length:</strong> ${data.dimensions?.length ? (data.dimensions.length * 2.54).toFixed(0) + " cm" : "N/A"}</p>
                <p><strong>Width:</strong> ${data.dimensions?.width ? (data.dimensions.width * 2.54).toFixed(0) + " cm" : "N/A"}</p>
                <p><strong>Height:</strong> ${data.dimensions?.height ? (data.dimensions.height * 2.54).toFixed(0) + " cm" : "N/A"}</p>
                <p><strong>Wheelbase:</strong> ${data.dimensions?.wheelbase || "N/A"} in</p>
                <p><strong>Cargo Capacity:</strong> ${data.dimensions?.cargo_capacity || "N/A"} cu ft</p>
                <p><strong>Max Towing:</strong> ${data.towing_capacity || "N/A"} lbs</p>
                </div>
            `;

          }
        })
        .catch(err => {
          console.error("Trim fetch failed", err);
          const specBox = document.getElementById("carSpecs");
          if (specBox) {
            specBox.innerHTML = "<p>Failed to load specifications</p>";
          }
        });

      // 🔄 Toggle specs
      const toggleBtn = document.getElementById("toggleSpecs");
      const specBox = document.getElementById("carSpecs");
      if (toggleBtn && specBox) {
        toggleBtn.addEventListener("click", () => {
          const visible = specBox.style.display !== "none";
          specBox.style.display = visible ? "none" : "block";
          toggleBtn.textContent = visible ? "Show More Details ⬇️" : "Hide Details ⬆️";
        });
      }

    // 📅 Booking form logic
      const form = document.getElementById("bookingForm");
      if (form) {
        form.addEventListener("submit", (e) => {
          e.preventDefault();

          const pickupDate = document.getElementById("pickupDate").value;
          const dropoffDate = document.getElementById("dropoffDate").value;
          const bookingMessage = document.getElementById("bookingMessage");
          const postBookingOptions = document.getElementById("postBookingOptions");

          if (!pickupDate || !dropoffDate) {
            bookingMessage.textContent = "Please select both pickup and drop-off dates.";
            bookingMessage.style.color = "red";
            return;
          }

          const start = new Date(pickupDate);
          const end = new Date(dropoffDate);
          if (end <= start) {
            bookingMessage.textContent = "Drop-off date must be after pickup date.";
            bookingMessage.style.color = "red";
            return;
          }

          if (!token || !userId) {
            bookingMessage.textContent = "Please log in to book a car.";
            bookingMessage.style.color = "red";
            return;
          }

          const days = Math.ceil((end - start) / (1000 * 60 * 60 * 24));
          const total = days * parseFloat(price);
          const pendingBooking = {
            user_id: userId,
            trim_id,
            pickup_location: location,
            dropoff_location: location,
            pickup_date: pickupDate,
            dropoff_date: dropoffDate,
            total_price: total.toFixed(2),
          };

          localStorage.setItem("pendingBooking", JSON.stringify(pendingBooking));

          fetch("http://localhost:5000/api/bookings", {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
              Authorization: `Bearer ${token}`
            },
            body: JSON.stringify(pendingBooking)
          })
            .then(res => {
              if (!res.ok) throw new Error("Booking failed.");
              return res.json();
            })
            .then(() => {
              bookingMessage.textContent = "Booking confirmed!";
              bookingMessage.style.color = "green";
              postBookingOptions.style.display = "block";
            })
            .catch(err => {
              console.error("Booking error", err);
              bookingMessage.textContent = "Booking failed. Please try again.";
              bookingMessage.style.color = "red";
            });
        });
      }

    }, 0); // ⏳ ensure DOM is rendered before running
  }, []);

    return (
    <>
      <Navbar />
      <div className="container fade-in">
        <div className="details-grid">

          {/* LEFT PANEL */}
          <div className="left-panel">
            <img id="carImage" className="car-img" src="" alt="Car Image" />
            <div className="car-info">
              <h2 id="carTitle"><span className="badge">🚗</span>Car Name</h2>
              <button id="wishlistBtn">🤍 Add to Wishlist</button>
              <div className="car-meta">
                <p><strong>Year:</strong> <span id="carYear"></span></p>
                <p><strong>Price:</strong> <span id="carPrice"></span></p>
                <p><strong>Type:</strong> <span id="carTypes"></span></p>
                <p><strong>Seats:</strong> <span id="carSeats"></span></p>
                <p><strong>Fuel:</strong> <span id="carFuel"></span></p>
                <p><strong>Transmission:</strong> <span id="carTransmission"></span></p>
                <p><strong>Drive Type:</strong> <span id="carDrive"></span></p>
                <p><strong>KMPL:</strong> <span id="carMPG"></span></p>
                <p><strong>Fuel Tank:</strong> <span id="carTank"></span></p>
              </div>
              <button id="toggleSpecs" className="toggle-btn">Show More Details ⬇️</button>
              <div id="carSpecs" className="specs-section" style={{ display: 'none' }}></div>
            </div>
          </div>

          {/* RIGHT PANEL */}
          <div className="right-panel">
            <div className="booking-form">
              <h2>Book This Car</h2>
              <form id="bookingForm">
                <label htmlFor="pickupDate">Pickup Date</label>
                <input type="date" id="pickupDate" name="pickupDate" required />

                <label htmlFor="dropoffDate">Drop-off Date</label>
                <input type="date" id="dropoffDate" name="dropoffDate" required />

                <label htmlFor="pickupLocation">Pickup Location</label>
                <input type="text" id="pickupLocation" name="pickupLocation" readOnly required />

                <label htmlFor="dropoffLocation">Drop-off Location</label>
                <input type="text" id="dropoffLocation" name="dropoffLocation" readOnly required />

                <button type="submit" id="confirmBooking">Confirm Booking</button>

                <div id="bookingMessage" style={{ marginTop: '10px', fontWeight: 'bold' }}></div>

                <div id="postBookingOptions" style={{ display: 'none', marginTop: '20px', textAlign: 'center' }}>
                  <p style={{ fontWeight: 'bold' }}>What would you like to do next?</p>
                  <button id="bookAnotherBtn">🚗 Book Another Car</button>
                  <button id="viewBookingsBtn">📋 View My Bookings</button>
                </div>
              </form>
            </div>
          </div>

        </div>
      </div>
    </>
  );
};

export default CarDetailsPage;
